mapper.delete:
DELETE FROM t_crude_base_price_interface
      WHERE 1=1 
        AND ID = #{id}

DELETE FROM t_crude_base_price_interface
      WHERE 1=1 
AND  ID = #{id}
AND  UUID = #{uuid}
AND  CRUDE_NAME_E = #{crudeNameE}
AND  CRUDE_NAME_C = #{crudeNameC}
AND  PRICING = #{pricing}
AND  PRICING_DATE = #{pricingDate}
AND  NOTE = #{note}
AND  REMARK = #{remark}
AND  EXTERNAL_SYSTEM = #{externalSystem}
AND  INTERFACE_DATE = #{interfaceDate}
AND  PROCESS_PERSON = #{processPerson}
AND  PROCESS_DATE = #{processDate}
AND  STATUS = #{status}
AND  ALIVE_FLAG = #{aliveFlag}

mapper.update:
		update t_crude_base_price_interface

         ID=#{id}
