		,CRUDE_NAME_C = #{crudeNameC}
		,PRICING = #{pricing}
		,PRICING_DATE = #{pricingDate}
		,NOTE = #{note}
		,REMARK = #{remark}
		,EXTERNAL_SYSTEM = #{externalSystem}
		,INTERFACE_DATE = #{interfaceDate}
		,PROCESS_PERSON = #{processPerson}
		,PROCESS_DATE = #{processDate}
		,STATUS = #{status}
		,ALIVE_FLAG = #{aliveFlag}

		where
       	 ID=#{id}
		and ALIVE_FLAG='1'

		update t_crude_base_price_interface

         ID=#{id}
		,UUID = #{uuid}
		,CRUDE_NAME_E = #{crudeNameE}
		,CRUDE_NAME_C = #{crudeNameC}
		,PRICING = #{pricing}
		,PRICING_DATE = #{pricingDate}
		,NOTE = #{note}
		,REMARK = #{remark}
		,EXTERNAL_SYSTEM = #{externalSystem}
		,INTERFACE_DATE = #{interfaceDate}
		,PROCESS_PERSON = #{processPerson}
		,PROCESS_DATE = #{processDate}
		,STATUS = #{status}
		,ALIVE_FLAG = #{aliveFlag}

		where
		ALIVE_FLAG='1'
		and  ID=#{id}
