		and  PROCESS_PERSON=#{processPerson}
		and  PROCESS_DATE=#{processDate}
		and  STATUS=#{status}
		and  ALIVE_FLAG=#{aliveFlag}

mapper.select:
select * from t_crude_base_price_interface
	where
 ALIVE_FLAG='1'
and  ID=#{id}
and  UUID=#{uuid}
and  CRUDE_NAME_E=#{crudeNameE}
and  CRUDE_NAME_C=#{crudeNameC}
and  PRICING=#{pricing}
and  PRICING_DATE=#{pricingDate}
and  NOTE=#{note}
and  REMARK=#{remark}
and  EXTERNAL_SYSTEM=#{externalSystem}
and  INTERFACE_DATE=#{interfaceDate}
and  PROCESS_PERSON=#{processPerson}
and  PROCESS_DATE=#{processDate}
and  STATUS=#{status}
and  ALIVE_FLAG=#{aliveFlag}

select
				  ID as "id" 
				 ,   UUID as "uuid" 
				 ,   CRUDE_NAME_E as "crudeNameE" 
				 ,   CRUDE_NAME_C as "crudeNameC" 
				 ,   PRICING as "pricing" 
				 ,   PRICING_DATE as "pricingDate" 
				 ,   NOTE as "note" 
				 ,   REMARK as "remark" 
				 ,   EXTERNAL_SYSTEM as "externalSystem" 
				 ,   INTERFACE_DATE as "interfaceDate" 
				 ,   PROCESS_PERSON as "processPerson" 
				 ,   PROCESS_DATE as "processDate" 
				 ,   STATUS as "status" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				from t_crude_base_price_interface
where
	ALIVE_FLAG='1'
	and  ID=#{id}
	and  UUID=#{uuid}
	and  CRUDE_NAME_E=#{crudeNameE}
	and  CRUDE_NAME_C=#{crudeNameC}
	and  PRICING=#{pricing}
	and  PRICING_DATE=#{pricingDate}
	and  NOTE=#{note}
	and  REMARK=#{remark}
	and  EXTERNAL_SYSTEM=#{externalSystem}
	and  INTERFACE_DATE=#{interfaceDate}
	and  PROCESS_PERSON=#{processPerson}
	and  PROCESS_DATE=#{processDate}
	and  STATUS=#{status}
	and  ALIVE_FLAG=#{aliveFlag}

select
count(*) from t_crude_base_price_interface 
where
 ALIVE_FLAG='1'
