mapper.select:
select * from t_crude_agio
	where
 ALIVE_FLAG='1'
and  ID=#{id}
and  UUID=#{uuid}
and  CRUDE_ID=#{crudeId}
and  PRICING=#{pricing}
and  PRICING_DATE=#{pricingDate}
and  LOADING_MONTH=#{loadingMonth}
and  TRANSPORT_CLAUSE=#{transportClause}
and  CHANGE1=#{change1}

and  ALIVE_FLAG=#{aliveFlag}
and  CREATE_DATE=#{createDate}
and  CREATE_PERSON=#{createPerson}
and  MODIFY_DATE=#{modifyDate}
and  MODIFY_PERSON=#{modifyPerson}
and  SOURCE=#{source}
and  INTERFACE_ID=#{interfaceId}
and  EXTEND1=#{extend1}
and  EXTEND2=#{extend2}
and  EXTEND3=#{extend3}
and  EXTEND4=#{extend4}
and  EXTEND5=#{extend5}
and  EXTEND6=#{extend6}
and  EXTEND7=#{extend7}
and  EXTEND8=#{extend8}
and  EXTEND9=#{extend9}
and  EXTEND10=#{extend10}
and PRICING_DATE=
	(SELECT max(t.PRICING_DATE) FROM t_crude_agio t where t.ALIVE_FLAG = '1')

select
				  ID as "id" 
				 ,   UUID as "uuid" 
				 ,   CRUDE_ID as "crudeId" 
				 ,   PRICING as "pricing" 
				 ,   PRICING_DATE as "pricingDate" 
				 ,   LOADING_MONTH as "loadingMonth" 
				 ,   TRANSPORT_CLAUSE as "transportClause" 
				 ,   CHANGE1 as "change1" 
				 ,   LATEST_FLAG as "latestFlag" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   CREATE_DATE as "createDate" 
				 ,   CREATE_PERSON as "createPerson" 
				 ,   MODIFY_DATE as "modifyDate" 
				 ,   MODIFY_PERSON as "modifyPerson" 
				 ,   SOURCE as "source" 
				 ,   INTERFACE_ID as "interfaceId" 
				 ,   EXTEND1 as "extend1" 
				 ,   EXTEND2 as "extend2" 
				 ,   EXTEND3 as "extend3" 
				 ,   EXTEND4 as "extend4" 
				 ,   EXTEND5 as "extend5" 
				 ,   EXTEND6 as "extend6" 
				 ,   EXTEND7 as "extend7" 
				 ,   EXTEND8 as "extend8" 
				 ,   EXTEND9 as "extend9" 
				 ,   EXTEND10 as "extend10" 
				from t_crude_agio
where
	ALIVE_FLAG='1'
	and  ID=#{id}
	and  UUID=#{uuid}
	and  CRUDE_ID=#{crudeId}
	and  PRICING=#{pricing}
	and  PRICING_DATE=#{pricingDate}
	and  LOADING_MONTH=#{loadingMonth}
	and  TRANSPORT_CLAUSE=#{transportClause}
	and  CHANGE1=#{change1}
	and  LATEST_FLAG=#{latestFlag}
	and  ALIVE_FLAG=#{aliveFlag}
	and  CREATE_DATE=#{createDate}
	and  CREATE_PERSON=#{createPerson}
	and  MODIFY_DATE=#{modifyDate}
	and  MODIFY_PERSON=#{modifyPerson}
	and  SOURCE=#{source}
	and  INTERFACE_ID=#{interfaceId}
	and  EXTEND1=#{extend1}
	and  EXTEND2=#{extend2}
	and  EXTEND3=#{extend3}
	and  EXTEND4=#{extend4}
	and  EXTEND5=#{extend5}
	and  EXTEND6=#{extend6}
	and  EXTEND7=#{extend7}
	and  EXTEND8=#{extend8}
	and  EXTEND9=#{extend9}
	and  EXTEND10=#{extend10}

