select
				  ID as "id" 
				 ,   UUID as "uuid" 
				 ,   INFO_GRAB_ID as "infoGrabId" 
				 ,   CHANNEL_ID as "channelId" 
				 ,   TITLE as "title" 
				 ,   SHORT_TITLE as "shortTitle" 
				 ,   GENERAL as "general" 
				 ,   ORIGIN as "origin" 
				 ,   ORIGIN_URL as "originUrl" 
				 ,   AUTHOR as "author" 
				 ,   INFORMATION_TYPE as "informationType" 
				 ,   SELECTED_ENDTIME as "selectedEndtime" 
				 ,   STICK as "stick" 
				 ,   STICK_EDNTIME as "stickEdntime" 
				 ,   HAS_TITLE_IMG as "hasTitleImg" 
				 ,   RECOMMEND_FLAG as "recommendFlag" 
				 ,   STATUS as "status" 
				 ,   BROWSE_COUNT as "browseCount" 
				 ,   COMMENT_COUNT as "commentCount" 
				 ,   SHARE_COUNT as "shareCount" 
				 ,   COLLECTION_COUNT as "collectionCount" 
				 ,   FABULOUS_COUNT as "fabulousCount" 
				 ,   TRAMPLE_COUNT as "trampleCount" 
				 ,   DESCRIPTION as "description" 
				 ,   VALID_BEGIN as "validBegin" 
				 ,   VALID_END as "validEnd" 
				 ,   VOID_PATH as "voidPath" 
				 ,   DISPLAY_MODE as "displayMode" 
				 ,   BIZ_ID as "bizId" 
				 ,   ARTICLE_TYPE as "articleType" 
				 ,   PROBATION_FLAG as "probationFlag" 
				 ,   IS_SHOW_GENERAL as "isShowGeneral" 
				 ,   IS_SHOW_ORIGIN as "isShowOrigin" 
				 ,   IS_SHOW_COPYRIGHT as "isShowCopyright" 
				 ,   IS_SHOW_DISCLAIMER as "isShowDisclaimer" 
				 ,   IS_SHOW_AUTHOR as "isShowAuthor" 
				 ,   RELEASE_ID as "releaseId" 
				 ,   DATE_FORMAT(RELEASE_DATE,'%Y-%m-%d') as "releaseDate" 
				 ,   REVIEWER_ID as "reviewerId" 
				 ,   REVIEWER as "reviewer" 
				 ,   AUDIT_TIME as "auditTime" 
				 ,   REVOKE_ID as "revokeId" 
				 ,   REVOKER as "revoker" 
				 ,   REVOKE_TIME as "revokeTime" 
				 ,   RESUFE_REMARK as "resufeRemark" 
				 ,   TAG_LIST as "tagList" 
				 ,   CREATE_USER as "createUser" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_USER as "updateUser" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   EXTEND1 as "extend1" 
				 ,   EXTEND2 as "extend2" 
				 ,   EXTEND3 as "extend3" 
				 ,   EXTEND4 as "extend4" 
				 ,   EXTEND5 as "extend5" 
				 ,   EXTEND6 as "extend6" 
				 ,   EXTEND7 as "extend7" 
				 ,   EXTEND8 as "extend8" 
				 ,   EXTEND9 as "extend9" 
				 ,   EXTEND10 as "extend10" 
				from t_cms_info
where
	ALIVE_FLAG='1'
	and  ID=#{id}
	and  UUID=#{uuid}
	and  INFO_GRAB_ID=#{infoGrabId}
	and  CHANNEL_ID=#{channelId}
	and  TITLE=#{title}
	and  SHORT_TITLE=#{shortTitle}
	and  GENERAL=#{general}
	and  ORIGIN=#{origin}
	and  ORIGIN_URL=#{originUrl}
	and  AUTHOR=#{author}
	and  INFORMATION_TYPE=#{informationType}
	and  SELECTED_ENDTIME=#{selectedEndtime}
	and  STICK=#{stick}
	and  STICK_EDNTIME=#{stickEdntime}
	and  HAS_TITLE_IMG=#{hasTitleImg}
	and  RECOMMEND_FLAG=#{recommendFlag}
	and  STATUS=#{status}
	and  BROWSE_COUNT=#{browseCount}
	and  COMMENT_COUNT=#{commentCount}
	and  SHARE_COUNT=#{shareCount}
	and  COLLECTION_COUNT=#{collectionCount}
	and  FABULOUS_COUNT=#{fabulousCount}
	and  TRAMPLE_COUNT=#{trampleCount}
	and  DESCRIPTION=#{description}

	and  VOID_PATH=#{voidPath}
	and  DISPLAY_MODE=#{displayMode}
	and  BIZ_ID=#{bizId}
	and  ARTICLE_TYPE=#{articleType}
	and  PROBATION_FLAG=#{probationFlag}
	and  IS_SHOW_GENERAL=#{isShowGeneral}
	and  IS_SHOW_ORIGIN=#{isShowOrigin}
	and  IS_SHOW_COPYRIGHT=#{isShowCopyright}
	and  IS_SHOW_DISCLAIMER=#{isShowDisclaimer}
	and  IS_SHOW_AUTHOR=#{isShowAuthor}
	and  RELEASE_ID=#{releaseId}
	and  RELEASE_DATE=#{releaseDate}
	and  REVIEWER_ID=#{reviewerId}
	and  REVIEWER=#{reviewer}
	and  AUDIT_TIME=#{auditTime}
	and  REVOKE_ID=#{revokeId}
	and  REVOKER=#{revoker}
	and  REVOKE_TIME=#{revokeTime}
	and  RESUFE_REMARK=#{resufeRemark}
	and  TAG_LIST=#{tagList}
	and  CREATE_USER=#{createUser}
	and  CREATE_DATE=#{createDate}
	and  UPDATE_USER=#{updateUser}
	and  UPDATE_DATE=#{updateDate}
	and  ALIVE_FLAG=#{aliveFlag}
	and  EXTEND1=#{extend1}
	and  EXTEND2=#{extend2}
	and  EXTEND3=#{extend3}
	and  EXTEND4=#{extend4}
	and  EXTEND5=#{extend5}
	and  EXTEND6=#{extend6}
	and  EXTEND7=#{extend7}
	and  EXTEND8=#{extend8}
	and  EXTEND9=#{extend9}
	and  EXTEND10=#{extend10}
	and  CHANNEL_ID=-1
	and  CHANNEL_ID!=-1
	and  DATE_FORMAT(RELEASE_DATE, '%Y-%m-%d') >= #{validBegin}
	and  DATE_FORMAT(RELEASE_DATE, '%Y-%m-%d') <= #{validEnd}
	order by id DESC

