mapper.update:
update t_cms_info

	STATUS = #{status}
	,UPDATE_USER = #{updateUser}
	,UPDATE_USER = #{updateUser}
	,RELEASE_ID = #{releaseId}
	,RELEASE_DATE = #{releaseDate}

where
ALIVE_FLAG='1'
 and  UUID=#{uuid}

update t_cms_info

	TITLE = #{title}
	, STATUS = #{status}
	,UPDATE_USER = #{updateUser}
	,UPDATE_DATE = #{updateDate}

where
ALIVE_FLAG='1'
 and  UUID=#{uuid}

		update t_cms_info

         ALIVE_FLAG = '0'
		,UPDATE_USER = #{updateUser}
		,UPDATE_DATE = #{updateDate}

		where
       	 ID=#{id}
		and ALIVE_FLAG='1' and STATUS = '00'
		and  UUID=#{uuid}

		update t_cms_info

			BROWSE_COUNT = #{browseCount}
			,COMMENT_COUNT = #{commentCount}
			,SHARE_COUNT = #{shareCount}
			,COLLECTION_COUNT = #{collectionCount}
			,FABULOUS_COUNT = #{fabulousCount}
			,TRAMPLE_COUNT = #{trampleCount}
			,UPDATE_USER = #{updateUser}
			,CREATE_USER = #{createUser}

		where
       	 ID=#{id} and ALIVE_FLAG='1' and  UUID=#{uuid}

mapper.select:
SELECT tci.ID AS "id", tci.UUID AS "uuid", tci.INFO_GRAB_ID AS "infoGrabId", tci.CHANNEL_ID AS "channelId", tci.TITLE AS "title", tci.SHORT_TITLE AS "shortTitle", tci.GENERAL AS "general", tci.ORIGIN AS "origin", tci.ORIGIN_URL AS "originUrl", tci.AUTHOR AS "author", tci.INFORMATION_TYPE AS "informationType", DATE_FORMAT( tci.SELECTED_ENDTIME, '%Y-%m-%d %T' ) AS "selectedEndtime", tci.STICK AS "stick", DATE_FORMAT( tci.STICK_EDNTIME, '%Y-%m-%d %T' ) AS "stickEdntime", tci.HAS_TITLE_IMG AS "hasTitleImg", tci.RECOMMEND_FLAG AS "recommendFlag", tci. STATUS AS "status", tci.BROWSE_COUNT AS "browseCount", tci.COMMENT_COUNT AS "commentCount", tci.SHARE_COUNT AS "shareCount", tci.COLLECTION_COUNT AS "collectionCount", tci.FABULOUS_COUNT AS "fabulousCount", tci.TRAMPLE_COUNT AS "trampleCount", tci.DESCRIPTION AS "description", DATE_FORMAT(tci.VALID_BEGIN, '%Y-%m-%d') AS "validBegin", DATE_FORMAT(tci.VALID_END, '%Y-%m-%d') AS "validEnd", tci.VOID_PATH AS "voidPath", tci.DISPLAY_MODE AS "displayMode", tci.BIZ_ID AS "bizId", tci.ARTICLE_TYPE AS "articleType", tci.PROBATION_FLAG AS "probationFlag", tci.IS_SHOW_GENERAL AS "isShowGeneral", tci.IS_SHOW_ORIGIN AS "isShowOrigin", tci.IS_SHOW_COPYRIGHT AS "isShowCopyright", tci.IS_SHOW_DISCLAIMER AS "isShowDisclaimer", tci.IS_SHOW_AUTHOR AS "isShowAuthor", tci.RELEASE_ID AS "releaseId", DATE_FORMAT( tci.RELEASE_DATE, '%Y-%m-%d' ) AS "releaseDate", tci.REVIEWER_ID AS "reviewerId", tci.REVIEWER AS "reviewer", DATE_FORMAT(tci.AUDIT_TIME, '%Y-%m-%d') AS "auditTime", tci.REVOKE_ID AS "revokeId", tci.REVOKER AS "revoker", DATE_FORMAT(tci.REVOKE_TIME, '%Y-%m-%d') AS "revokeTime", tci.RESUFE_REMARK AS "resufeRemark", tci.TAG_LIST AS "tagList", tci.CREATE_USER AS "createUser", DATE_FORMAT(tci.CREATE_DATE, '%Y-%m-%d') AS "createDate", tci.UPDATE_USER AS "updateUser", DATE_FORMAT(tci.UPDATE_DATE, '%Y-%m-%d') AS "updateDate", tci.ALIVE_FLAG AS "aliveFlag", tci.EXTEND1 AS "extend1", tci.EXTEND2 AS "extend2", tci.EXTEND3 AS "extend3", tci.EXTEND4 AS "extend4", tci.EXTEND5 AS "extend5", tci.EXTEND6 AS "extend6", tci.EXTEND7 AS "extend7", tci.EXTEND8 AS "extend8", tci.EXTEND9 AS "extend9", tci.EXTEND10 AS "extend10", sub.CHANNEL_NAME AS "channelName", m.CHANNEL_M_NAME as "channelMName" FROM t_cms_info tci, t_cms_info_content c, t_cms_channel_sub sub, t_cms_channel_m m WHERE tci.ALIVE_FLAG='1' AND tci.ID = c.INFO_ID AND tci.CHANNEL_ID = sub.ID AND sub.CHANNEL_M_ID = m.ID AND tci.CHANNEL_ID > 0 AND m.CHANNEL_M_DESC = #{infoType}
and m.uuid = #{channelMUuid}
and sub.uuid = #{channelSubUuid}
and ( tci.TITLE like concat('%', trim(#{keyWords}), '%') or m.CHANNEL_M_NAME like concat('%', trim(#{keyWords}), '%') or m.CHANNEL_M_CODE like concat('%', trim(#{keyWords}), '%') )
and DATE_FORMAT(tci.RELEASE_DATE,"%Y-%m-%d") >= DATE_FORMAT(#{pubBeginTime},"%Y-%m-%d")
and DATE_FORMAT(tci.RELEASE_DATE,"%Y-%m-%d") <= DATE_FORMAT(#{pubEndTime},"%Y-%m-%d")
and tci.INFORMATION_TYPE = #{informationType}
and tci.STATUS != '30'
and tci.STATUS = #{status}
and tci.STATUS = '30'
order by tci.ID desc

