mapper.select:
select * from t_cms_info where ALIVE_FLAG='1' and CREATE_USER = #{userName}
and STATUS = '30'
and (STATUS = '00' or STATUS = '20' or STATUS = '29')
and STATUS = '10'
and STATUS = #{status}
and CHANNEL_ID in (${channelIds})
and CHANNEL_ID = #{channelId}
order by ID desc

select i.ID as id, i.CHANNEL_ID as channelId, i.TITLE AS "title", IFNULL(author, '平台') AS "author", i.BROWSE_COUNT AS "browseCount", DATE_FORMAT(i.RELEASE_DATE, '%Y-%m-%d %H:%i:%s') AS "releaseDate", c.TEX AS "text", c.TEX_HTML AS "textHtml", i.TAG_LIST AS "tagList", s.channel_name AS "channelName", i.ORIGIN as "origin", i.IS_SHOW_COPYRIGHT as "isShowCopyright", i.IS_SHOW_DISCLAIMER as "isShowDisclaimer" FROM t_cms_info i, t_cms_info_content c, t_cms_channel_sub s WHERE i.ALIVE_FLAG = '1' AND i.ID = c.INFO_ID AND i.CHANNEL_ID = s.ID and i.UUID=#{infoUuid}
AND s.uuid = #{channelUuid}
AND i.TITLE = #{title}
AND EXISTS ( SELECT 1 FROM t_cms_channel_m m WHERE s.channel_m_id = m.id AND m.uuid = #{channelMUuid} )

mapper.update:
update t_cms_info set BROWSE_COUNT = BROWSE_COUNT + 1
	where ALIVE_FLAG='1'
and  ID = #{id}

	update t_cms_info

	BROWSE_COUNT = #{browseCount}

	where
		ALIVE_FLAG='1'
and  UUID=#{uuid}

mapper.select:
select 
	i.RELEASE_DATE AS releaseDate,
	c.TEX AS tex,
	i.TITLE AS title,
	i.UUID AS uuid
from t_cms_info i,
t_cms_info_content c
where i.id = c.INFO_ID AND i.ALIVE_FLAG='1'
AND i.BIZ_ID = #{bizId}
AND i.STATUS = #{status}
AND i.ARTICLE_TYPE = #{articleType}
ORDER BY i.RELEASE_DATE DESC
