mapper.select:
select * from t_order_settlement
	where
 ALIVE_FLAG='1'
and  ORDER_SETTLEMENT_ID=#{orderSettlementId}
and  ORDER_SETTLEMENT_UUID=#{orderSettlementUuid}
and  ORDER_ID=#{orderId}
and  ORDER_STATEMENT_ID=#{orderStatementId}
and  ORDER_SETTLEMENT_NO=#{orderSettlementNo}
and  SETTLEMENT_TYPE=#{settlementType}
and  BILL_WEIGHT_BBL=#{billWeightBbl}
and  BILL_WEIGHT_T=#{billWeightT}
and  TOTAL_FEE=#{totalFee}
and  PRICE=#{price}
and  TOTAL_AMOUNT=#{totalAmount}
and  PAYMENT_DATE=#{paymentDate}
and  BILL_DATE=#{billDate}
and  PAYEE=#{payee}
and  ACOUNT=#{acount}
and  BANK_NAME=#{bankName}
and  STATUS=#{status}
and  SUBMIT_PERSON=#{submitPerson}
and  SUBMIT_PERSON_NAME=#{submitPersonName}
and  CONFIRM_PERSON=#{confirmPerson}
and  CONFIRM_PERSON_NAME=#{confirmPersonName}
and  CUSTOMER_NAME=#{customerName}
and  CONTACT_NAME=#{contactName}
and  TEL=#{tel}
and  EMAIL=#{email}
and  CHECK_TIME=#{checkTime}
and  CONTRACT_NO=#{contractNo}
and  AGENT_CONTRACT=#{agentContract}
and  OIL=#{oil}
and  PRICE_FORMULA=#{priceFormula}
and  SHIP_NAME=#{shipName}
and  BASE_PRICE=#{basePrice}
and  AGIO=#{agio}
and  CONTRACT_QUANTITY=#{contractQuantity}
and  TRADE_TERM=#{tradeTerm}
and  SETTLEMENT_PRICE=#{settlementPrice}
and  GOODS_AMOUNT=#{goodsAmount}
and  PAY_DATE=#{payDate}
and  SETTLEMENT_QUANTITY_BBL=#{settlementQuantityBbl}
and  SETTLED_AMOUNT=#{settledAmount}
and  SELLER_CUSTOMER_ID=#{sellerCustomerId}
and  BUYER_CUSTOMER_ID=#{buyerCustomerId}
and  LANG_VER=#{langVer}
and  ALIVE_FLAG=#{aliveFlag}
and  CREATE_USER=#{createUser}
and  CREATE_DATE=#{createDate}
and  UPDATE_USER=#{updateUser}
and  UPDATE_DATE=#{updateDate}
and  VERSION=#{version}

select
				  ORDER_SETTLEMENT_ID as "orderSettlementId" 
				 ,   ORDER_SETTLEMENT_UUID as "orderSettlementUuid" 
				 ,   ORDER_ID as "orderId" 
				 ,   ORDER_STATEMENT_ID as "orderStatementId" 
				 ,   ORDER_SETTLEMENT_NO as "orderSettlementNo" 
				 ,   SETTLEMENT_TYPE as "settlementType" 
				 ,   BILL_WEIGHT_BBL as "billWeightBbl" 
				 ,   BILL_WEIGHT_T as "billWeightT" 
				 ,   TOTAL_FEE as "totalFee" 
				 ,   PRICE as "price" 
				 ,   TOTAL_AMOUNT as "totalAmount" 
				 ,   PAYMENT_DATE as "paymentDate" 
				 ,   BILL_DATE as "billDate" 
				 ,   PAYEE as "payee" 
				 ,   ACOUNT as "acount" 
				 ,   BANK_NAME as "bankName" 
				 ,   STATUS as "status" 
				 ,   SUBMIT_PERSON as "submitPerson" 
				 ,   SUBMIT_PERSON_NAME as "submitPersonName" 
				 ,   CONFIRM_PERSON as "confirmPerson" 
				 ,   CONFIRM_PERSON_NAME as "confirmPersonName" 
				 ,   CUSTOMER_NAME as "customerName" 
				 ,   CONTACT_NAME as "contactName" 
				 ,   TEL as "tel" 
				 ,   EMAIL as "email" 
				 ,   CHECK_TIME as "checkTime" 
				 ,   CONTRACT_NO as "contractNo" 
				 ,   AGENT_CONTRACT as "agentContract" 
				 ,   OIL as "oil" 
				 ,   PRICE_FORMULA as "priceFormula" 
				 ,   SHIP_NAME as "shipName" 
				 ,   BASE_PRICE as "basePrice" 
				 ,   AGIO as "agio" 
				 ,   CONTRACT_QUANTITY as "contractQuantity" 
				 ,   TRADE_TERM as "tradeTerm" 
				 ,   SETTLEMENT_PRICE as "settlementPrice" 
				 ,   GOODS_AMOUNT as "goodsAmount" 
				 ,   PAY_DATE as "payDate" 
				 ,   SETTLEMENT_QUANTITY_BBL as "settlementQuantityBbl" 
				 ,   SETTLED_AMOUNT as "settledAmount" 
				 ,   SELLER_CUSTOMER_ID as "sellerCustomerId" 
				 ,   BUYER_CUSTOMER_ID as "buyerCustomerId" 
				 ,   LANG_VER as "langVer" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   CREATE_USER as "createUser" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_USER as "updateUser" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   VERSION as "version" 
				from t_order_settlement
where
	ALIVE_FLAG='1'
	and  ORDER_SETTLEMENT_ID=#{orderSettlementId}
	and  ORDER_SETTLEMENT_UUID=#{orderSettlementUuid}
	and  ORDER_ID=#{orderId}
	and  ORDER_STATEMENT_ID=#{orderStatementId}
	and  ORDER_SETTLEMENT_NO like '%${orderSettlementNo}%'
	and  SETTLEMENT_TYPE=#{settlementType}
	and  BILL_WEIGHT_BBL=#{billWeightBbl}
	and  BILL_WEIGHT_T=#{billWeightT}
	and  TOTAL_FEE=#{totalFee}
	and  PRICE=#{price}
	and  TOTAL_AMOUNT=#{totalAmount}
	and  PAYMENT_DATE=#{paymentDate}
	and  BILL_DATE=#{billDate}
	and  PAYEE=#{payee}
	and  ACOUNT=#{acount}
	and  BANK_NAME=#{bankName}
	and  STATUS=#{status}
	and  SUBMIT_PERSON=#{submitPerson}
	and  SUBMIT_PERSON_NAME=#{submitPersonName}
	and  CONFIRM_PERSON=#{confirmPerson}
	and  CONFIRM_PERSON_NAME=#{confirmPersonName}
	and  CUSTOMER_NAME like '%${customerName}%'
	and  CONTACT_NAME like '%${contactName}%'
	and  TEL=#{tel}
	and  EMAIL=#{email}
	AND CHECK_TIME > #{createDateStart}
	AND CHECK_TIME < #{createDateEnd}
	and  CONTRACT_NO=#{contractNo}
	and  AGENT_CONTRACT=#{agentContract}
	and  OIL=#{oil}
	and  PRICE_FORMULA=#{priceFormula}
	and  SHIP_NAME=#{shipName}
	and  BASE_PRICE=#{basePrice}
	and  AGIO=#{agio}
	and  CONTRACT_QUANTITY=#{contractQuantity}
	and  TRADE_TERM=#{tradeTerm}
	and  SETTLEMENT_PRICE=#{settlementPrice}
	and  GOODS_AMOUNT=#{goodsAmount}
	and  PAY_DATE=#{payDate}
	and  SETTLEMENT_QUANTITY_BBL=#{settlementQuantityBbl}
	and  SETTLED_AMOUNT=#{settledAmount}
	and  SELLER_CUSTOMER_ID=#{sellerCustomerId}
	and  BUYER_CUSTOMER_ID=#{buyerCustomerId}
	and  LANG_VER=#{langVer}
	and  ALIVE_FLAG=#{aliveFlag}
	and  CREATE_USER=#{createUser}
	and  CREATE_DATE=#{createDate}
	and  UPDATE_USER=#{updateUser}
	and  UPDATE_DATE=#{updateDate}
	and  VERSION=#{version}
order by 
	CREATE_DATE DESC

