SELECT tos.ORDER_STATEMENT_ID AS orderStatementId ,DATE_FORMAT(tos.CHECK_TIME, '%Y-%m-%d %T') AS "checkTime" ,tos.CONFIRM_PERSON AS confirmPerson ,tos.CUSTOMER_NAME AS customerName ,tos.CONTACT_NAME AS contactName ,tos.TEL AS tel ,tos.ORDER_ID AS orderId ,tos.`STATUS` AS 'status' ,tos.TOTAL_AMOUNT AS totalAmount FROM t_order tor INNER JOIN t_order_settlement tos ON tos.ORDER_ID = tor.ID AND tos.ALIVE_FLAG = '1'
WHERE (tor.BUYER_CUSTOMER_ID = #{epMemberId} OR tor.SELLER_CUSTOMER_ID = #{epMemberId} OR tor.AGENT_ID = #{epMemberId}) AND tor.ALIVE_FLAG = '1'
AND tos.ORDER_STATEMENT_ID LIKE CONCAT('%', #{orderStatementId}, '%')
AND tos.CHECK_TIME LIKE CONCAT('%', #{checkTime}, '%')
AND tos.CONFIRM_PERSON = #{confirmPerson}
AND tos.CUSTOMER_NAME = #{customerName}
AND tos.CONTACT_NAME = #{contactName}
AND tos.TEL = #{tel}
