select
				  ID as "id" 
				 ,   UUID as "uuid" 
				 ,   ORDER_NO as "orderNo" 
				 ,   WAYBILL_UUID as "waybillUuid" 
				 ,   LOAD_PORT as "loadPort" 
				 ,   OIL as "oil" 
				 ,   ETA as "eta" 
				 ,   EX_BERTH as "exBerth" 
				 ,   NOR_DATE as "norDate" 
				 ,   WATER_DATE as "waterDate" 
				 ,   ATRIP_DATE as "atripDate" 
				 ,   BERTH_DATE as "berthDate" 
				 ,   AC_START as "acStart" 
				 ,   AC_FINISH as "acFinish" 
				 ,   EX_LEAVE as "exLeave" 
				 ,   AC_LEAVE as "acLeave" 
				 ,   REM_TUBE_DATE as "remTubeDate" 
				 ,   INSPECTION as "inspection" 
				 ,   INSPECTION_MEMBERID as "inspectionMemberid" 
				 ,   INSPECTION_TERMINAL as "inspectionTerminal" 
				 ,   INSPECTION_CONTACT as "inspectionContact" 
				 ,   INSPECTION_EMAIL as "inspectionEmail" 
				 ,   INSPECTION_TEL as "inspectionTel" 
				 ,   INSPECTION_CONTENT as "inspectionContent" 
				 ,   AGENCY as "agency" 
				 ,   EP_MEMBER_ID as "epMemberId" 
				 ,   BL_DATE as "blDate" 
				 ,   BL_STATUS as "blStatus" 
				 ,   BL_NIGHTSTOOL as "blNightstool" 
				 ,   BL_HAIR_BARREL as "blHairBarrel" 
				 ,   BL_NET_TONNAGE as "blNetTonnage" 
				 ,   BL_HAIR_TONNAGE as "blHairTonnage" 
				 ,   API as "api" 
				 ,   WATER_IMP_CON as "waterImpCon" 
				 ,   WATER_QUANTITY as "waterQuantity" 
				 ,   SH_HAIR_BARREL as "shHairBarrel" 
				 ,   SH_HAIR_TONNAGE as "shHairTonnage" 
				 ,   SH_NET_BARREL as "shNetBarrel" 
				 ,   SH_NET_TONNAGE as "shNetTonnage" 
				 ,   SH_GROSS_CUBIC_METER as "shGrossCubicMeter" 
				 ,   SH_NET_CUBIC_METER as "shNetCubicMeter" 
				 ,   SH_DENSITY as "shDensity" 
				 ,   RATIO_BARREL as "ratioBarrel" 
				 ,   RATIO_TONNAGE as "ratioTonnage" 
				 ,   QUANTITY as "quantity" 
				 ,   REMARK as "remark" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   VERSION as "version" 
				 ,   LANG_VER as "langVer" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   CREATE_USER as "createUser" 
				 ,   UPDATE_USER as "updateUser" 
				from t_order_ship_loadinginfo
where
