select * from t_ship_agreement
where
	 ALIVE_FLAG='1'
and  PALLET_ID=#{palletId}
and  PALLET_UUID=#{palletUuid}
and  SYS_SHIP_ID=#{sysShipId}
and  STATUS IN ('1','2','3','4','5','6','7','8')

select
				  AGREEMENT_ID as "agreementId" 
				 ,   UUID as "uuid" 
				 ,   AGREEMENT_CODE as "agreementCode" 
				 ,   EP_MEMBER_ID as "epMemberId" 
				 ,   ORDER_CODE as "orderCode" 
				 ,   ORDER_NO as "orderNo" 
				 ,   SIGN_DATE as "signDate" 
				 ,   PALLET_ID as "palletId" 
				 ,   PALLET_UUID as "palletUuid" 
				 ,   FIR_PARTY as "firParty" 
				 ,   SEC_PARTY as "secParty" 
				 ,   SHIP_NAME as "shipName" 
				 ,   SYS_SHIP_UUID as "sysShipUuid" 
				 ,   SYS_SHIP_ID as "sysShipId" 
				 ,   STATUS as "status" 
				 ,   ALL_QUANTITY as "allQuantity" 
				 ,   QUANTITY as "quantity" 
				 ,   OIL_TYPE as "oilType" 
				 ,   LOAD_PORT as "loadPort" 
				 ,   UNLOAD_PORT as "unloadPort" 
				 ,   LAYCAN as "laycan" 
				 ,   ACCESSORY as "accessory" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   VERSION as "version" 
				 ,   LANG_VER as "langVer" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   CREATE_USER as "createUser" 
				 ,   UPDATE_USER as "updateUser" 
				 ,   EXT1 as "ext1" 
				 ,   ACCESSORY_PATH as "accessoryPath" 
				 ,	 EP_MEMBER_NAME as epMemberName
				 ,	 CLAUSE_ID as clauseId
				 ,	 CLAUSE_UUID as clauseUuid
				,	CARRIER_NAME as carrierName
				,	SHIP_OWNER as shipOwner
				,	MIN_QUANTITY as minQuantity
				,	SHIP_OWNER_ID as shipOwnerId
				 ,   EP_MEMBER_NAME_EN as "epMemberNameEn" 
				 ,   SHIP_OWNER_EN as "shipOwnerEn" 
				 ,   CARRIER_NAME_EN as "carrierNameEn" 
				 ,   BROKER_NAME_EN as "brokerNameEn" 
				 ,   QUANTITY_EN as "quantityEn" 
				 ,   LOAD_PORT_EN as "loadPortEn" 
				 ,   LOAD_PORT_CODE as "loadPortCode" 
				 ,   UNLOAD_PORT_EN as "unloadPortEn" 
				 ,   UNLOAD_PORT_CODE as "unloadPortCode" 
				 ,   OIL_TYPE_EN as "oilTypeEn" 
				 ,   OIL_TYPE_CODE as "oilTypeCode" 
				 ,   LOAD_PORT_COPY_EN as "loadPortCopyEn" 
				 ,   UNLOAD_PORT_COPY_EN as "unloadPortCopyEn" 
				 ,   OIL_TYPE_COPY_EN as "oilTypeCopyEn" 

				from t_ship_agreement 

where
	ALIVE_FLAG='1'
	and  AGREEMENT_ID=#{agreementId}
	and  UUID=#{uuid}
	and  AGREEMENT_CODE like CONCAT('%',#{agreementCode},'%')
	and  EP_MEMBER_ID=#{epMemberId}
	and  ORDER_CODE=#{orderCode}
	and  ORDER_NO=#{orderNo}
	and  SIGN_DATE=#{signDate}
	and  PALLET_ID=#{palletId}
	and  PALLET_UUID=#{palletUuid}
	and  FIR_PARTY=#{firParty}
	and  SEC_PARTY=#{secParty}
	and  SHIP_NAME=#{shipName}
	and  SYS_SHIP_UUID=#{sysShipUuid}
	and  SYS_SHIP_ID=#{sysShipId}
	and  STATUS=#{status}
	and  ALL_QUANTITY=#{allQuantity}
	and  QUANTITY=#{quantity}
	and  OIL_TYPE   like CONCAT('%',#{oilType},'%')
	and  LOAD_PORT=#{loadPort}
	and  UNLOAD_PORT=#{unloadPort}
	and  LAYCAN=#{laycan}
	and  ACCESSORY=#{accessory}
	and  ALIVE_FLAG=#{aliveFlag}
	and  VERSION=#{version}
	and  LANG_VER=#{langVer}
	and  CREATE_DATE=#{createDate}
	and  UPDATE_DATE=#{updateDate}
	and  CREATE_USER=#{createUser}
	and  UPDATE_USER=#{updateUser}
	and  EXT1=#{ext1}
	and  ACCESSORY_PATH=#{accessoryPath}
	and EP_MEMBER_NAME = #{epMemberName}
	and CLAUSE_ID = #{clauseId}
	and CLAUSE_UUID = #{clauseUuid}
	and SHIP_OWNER_ID = #{shipOwnerId}
	and  EP_MEMBER_NAME_EN=#{epMemberNameEn}
	and  SHIP_OWNER_EN=#{shipOwnerEn}
	and  CARRIER_NAME_EN=#{carrierNameEn}
	and  BROKER_NAME_EN=#{brokerNameEn}
	and  QUANTITY_EN=#{quantityEn}
	and  LOAD_PORT_EN=#{loadPortEn}
	and  LOAD_PORT_CODE=#{loadPortCode}
	and  UNLOAD_PORT_EN=#{unloadPortEn}
	and  UNLOAD_PORT_CODE=#{unloadPortCode}
	and  OIL_TYPE_EN=#{oilTypeEn}
	and  OIL_TYPE_CODE=#{oilTypeCode}
	and  LOAD_PORT_COPY_EN=#{loadPortCopyEn}
	and  UNLOAD_PORT_COPY_EN=#{unloadPortCopyEn}
	and  OIL_TYPE_COPY_EN=#{oilTypeCopyEn}
	and PALLET_UUID IN (
		SELECT
			p.UUID
		FROM
			t_ship_pallet p
		WHERE
			p.ALIVE_FLAG = "1"
		AND p.EP_MEMBER_ID =#{member}
		)

	ORDER BY STATUS ASC,CREATE_DATE DESC
