select * from t_ship_disburden
where
	 ALIVE_FLAG='1'
	and  DISBURDEN_ID=#{disburdenId}
	and  UUID=#{uuid}
	and  UNLOAD_PORT_UUID=#{unloadPortUuid}
	and  UNLOAD_PORT=#{unloadPort}
	and  OIL_TYPE=#{oilType}
	and  AGREEMENT_CODE=#{agreementCode}
	and  PALLET_ID=#{palletId}
	and  PALLET_UUID=#{palletUuid}
	and  WAYBILL_UUID=#{waybillUuid}
	and  WAYBILL_ID=#{waybillId}
