	where
												 INTENTION_ID=#{intentionId}

mapper.select:
select
				  INTENTION_ID as "intentionId" 
				 ,   UUID as "uuid" 
				 ,   INTENTION_CODE as "intentionCode" 
				 ,   PALLET_ID as "palletId" 
				 ,   PALLET_UUID as "palletUuid" 
				 ,   SHIP_PLATE_ID as "shipPlateId" 
				 ,   SHIP_PLATE_UUID as "shipPlateUuid" 
				 ,   EP_MEMBER_ID as "epMemberId" 
				 ,   EP_MEMBER_NAME as "epMemberName" 
				 ,   SHIP_PLATE_MEMBER_ID as "shipPlateMemberId" 
				 ,   SHIP_PLATE_MEMBER_NAME as "shipPlateMemberName" 
				 ,   SHIP_PLATE_MEMBER_SIM_NAME as "shipPlateMemberSimName" 
				 ,   STATUS as "status" 
				 ,   IN_PACT_BEG as "inPactBeg" 
				 ,   IN_PACT_END as "inPactEnd" 
				 ,   IN_MIN_QUANTITY as "inMinQuantity" 
				 ,   IN_WS as "inWs" 
				 ,   IN_PACT_SPEED as "inPactSpeed" 
				 ,   IN_DEMURRAGE as "inDemurrage" 
				 ,   IN_DOCK_TIME as "inDockTime" 
				 ,   IN_UNLOAD_REGION as "inUnloadRegion" 
				 ,   IN_LOAD_REGION as "inLoadRegion" 
				 ,   IN_COMMISSION as "inCommission" 
				 ,   IN_REMARK as "inRemark" 
				 ,   CO_PACT_BEG as "coPactBeg" 
				 ,   CO_PACT_END as "coPactEnd" 
				 ,   CO_MIN_QUANTITY as "coMinQuantity" 
				 ,   CO_WS as "coWs" 
				 ,   CO_PACT_SPEED as "coPactSpeed" 
				 ,   CO_DEMURRAGE as "coDemurrage" 
				 ,   CO_DOCK_TIME as "coDockTime" 
				 ,   CO_UNLOAD_REGION as "coUnloadRegion" 
				 ,   CO_LOAD_REGION as "coLoadRegion" 
				 ,   CO_COMMISSION as "coCommission" 
				 ,   CO_REMARK as "coRemark" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   VERSION as "version" 
				 ,   LANG_VER as "langVer" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   CREATE_USER as "createUser" 
				 ,   UPDATE_USER as "updateUser" 
				 ,   EXT1 as "ext1" 
				 ,   EP_MEMBER_NAME_EN as "epMemberNameEn" 
				 ,   SHIP_PLATE_MEMBER_NAME_EN as "shipPlateMemberNameEn" 
				from t_ship_intention
where
	ALIVE_FLAG='1'
	and  INTENTION_ID=#{intentionId}
	and  UUID=#{uuid}
	and  INTENTION_CODE LIKE CONCAT('%',#{intentionCode},'%')
	and  PALLET_ID=#{palletId}
	and  PALLET_UUID=#{palletUuid}
	and  SHIP_PLATE_ID=#{shipPlateId}
	and  SHIP_PLATE_UUID=#{shipPlateUuid}
	and  EP_MEMBER_ID=#{epMemberId}
	and  EP_MEMBER_NAME=#{epMemberName}
	and  SHIP_PLATE_MEMBER_ID=#{shipPlateMemberId}
	and  SHIP_PLATE_MEMBER_NAME=#{shipPlateMemberName}
	and  SHIP_PLATE_MEMBER_SIM_NAME=#{shipPlateMemberSimName}
	and  STATUS=#{status}
	and  IN_PACT_BEG=#{inPactBeg}
	and  IN_PACT_END=#{inPactEnd}
	and  IN_MIN_QUANTITY=#{inMinQuantity}
	and  IN_WS=#{inWs}
	and  IN_PACT_SPEED=#{inPactSpeed}
	and  IN_DEMURRAGE=#{inDemurrage}
	and  IN_DOCK_TIME=#{inDockTime}
	and  IN_UNLOAD_REGION=#{inUnloadRegion}
	and  IN_LOAD_REGION=#{inLoadRegion}
	and  IN_COMMISSION=#{inCommission}
	and  IN_REMARK=#{inRemark}
	and  CO_PACT_BEG=#{coPactBeg}
	and  CO_PACT_END=#{coPactEnd}
	and  CO_MIN_QUANTITY=#{coMinQuantity}
	and  CO_WS=#{coWs}
	and  CO_PACT_SPEED=#{coPactSpeed}
	and  CO_DEMURRAGE=#{coDemurrage}
	and  CO_DOCK_TIME=#{coDockTime}
	and  CO_UNLOAD_REGION=#{coUnloadRegion}
	and  CO_LOAD_REGION=#{coLoadRegion}
	and  CO_COMMISSION=#{coCommission}
	and  CO_REMARK=#{coRemark}
	and  ALIVE_FLAG=#{aliveFlag}
	and  VERSION=#{version}
	and  LANG_VER=#{langVer}
	and  CREATE_DATE=#{createDate}
	and  UPDATE_DATE=#{updateDate}
	and  CREATE_USER=#{createUser}
	and  UPDATE_USER=#{updateUser}
	and  EXT1=#{ext1}
	and  EP_MEMBER_NAME_EN=#{epMemberNameEn}
	and  SHIP_PLATE_MEMBER_NAME_EN=#{shipPlateMemberNameEn}

