mapper.select:
select
				  SHIP_PACT_ID as "shipPactId" 
				 ,   UUID as "uuid" 
				 ,   PACT_CODE as "pactCode" 
				 ,   EP_MEMBER_ID as "epMemberId" 
				 ,   STATUS as "status" 
				 ,   LOAD_PORT as "loadPort" 
				 ,   DETAIL_STATUS as "detailStatus" 
				 ,   SHIP_PLATE_ID as "shipPlateId" 
				 ,   SHIP_PLATE_UUID as "shipPlateUuid" 
				 ,   SIGN_DATE as "signDate" 
				 ,   CARRIER_ID as "carrierId" 
				 ,   CARRIER_NAME as "carrierName" 
				 ,   SHIP_OWNER as "shipOwner" 
				 ,   SHIP_NAME as "shipName" 
				 ,   PACT_BEG as "pactBeg" 
				 ,   PACT_END as "pactEnd" 
				 ,   PACT_TEXT as "pactText" 
				 ,   MIN_QUANTITY as "minQuantity" 
				 ,   WS as "ws" 
				 ,   WS_RES as "wsRes" 
				 ,   PACT_SPEED as "pactSpeed" 
				 ,   DEMURRAGE as "demurrage" 
				 ,   BROKER_ID as "brokerId" 
				 ,   BROKER_NAME as "brokerName" 
				 ,   DOCK_TIME as "dockTime" 
				 ,   ACCESSORY as "accessory" 
				 ,   ACCESSORY_PATH as "accessoryPath" 
				 ,   MMSI as "mmsi" 
				 ,   REMARK as "remark" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   VERSION as "version" 
				 ,   LANG_VER as "langVer" 
				 ,   CREATE_DATE as "createDate" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   CREATE_USER as "createUser" 
				 ,   UPDATE_USER as "updateUser" 
				 ,   EXT1 as "ext1" 
				 ,   SHIP_INTENTION_ID as "shipIntentionId" 
				 ,   SHIP_INTENTION_UUID as "shipIntentionUuid" 
				 ,   LOADING_AREA as "loadingArea" 
				 ,   UNLOADING_AREA as "unloadingArea" 
				 ,   UNLOADING_AREA as "unloadingArea" 
				 ,   SHIP_OWNER_ID as "shipOwnerId" 
				 ,   EP_MEMBER_NAME_EN as "epMemberNameEn" 
				 ,   SHIP_OWNER_EN as "shipOwnerEn" 
				 ,   CARRIER_NAME_EN as "carrierNameEn" 
				 ,   BROKER_NAME_EN as "brokerNameEn" 
				 ,   SYS_SHIP_UUID as "sysShipUuid" 
				from t_ship_ship_pact
where
	ALIVE_FLAG='1'
	and  SHIP_PACT_ID=#{shipPactId}
	and  UUID=#{uuid}
	and  PACT_CODE=#{pactCode}
	and  EP_MEMBER_ID=#{epMemberId}
	and  STATUS=#{status}
	and  LOAD_PORT=#{loadPort}
	and  DETAIL_STATUS=#{detailStatus}
	and  SHIP_PLATE_ID=#{shipPlateId}
	and  SHIP_PLATE_UUID=#{shipPlateUuid}
	and  SIGN_DATE=#{signDate}
	and  CARRIER_ID=#{carrierId}
	and  CARRIER_NAME=#{carrierName}
	and  SHIP_OWNER=#{shipOwner}
	and  SHIP_NAME=#{shipName}
	and  PACT_BEG=#{pactBeg}
	and  PACT_END=#{pactEnd}
	and  PACT_TEXT=#{pactText}
	and  MIN_QUANTITY=#{minQuantity}
	and  WS=#{ws}
	and  WS_RES=#{wsRes}
	and  PACT_SPEED=#{pactSpeed}
	and  DEMURRAGE=#{demurrage}
	and  BROKER_ID=#{brokerId}
	and  BROKER_NAME=#{brokerName}
	and  DOCK_TIME=#{dockTime}
	and  ACCESSORY=#{accessory}
	and  ACCESSORY_PATH=#{accessoryPath}
	and  MMSI=#{mmsi}
	and  REMARK=#{remark}
	and  ALIVE_FLAG=#{aliveFlag}
	and  VERSION=#{version}
	and  LANG_VER=#{langVer}
	and  CREATE_DATE=#{createDate}
	and  UPDATE_DATE=#{updateDate}
	and  CREATE_USER=#{createUser}
	and  UPDATE_USER=#{updateUser}
	and  EXT1=#{ext1}
	and  SHIP_INTENTION_ID=#{shipIntentionId}
	and  SHIP_INTENTION_UUID=#{shipIntentionUuid}
	and  LOADING_AREA=#{loadingArea}
	and  UNLOADING_AREA=#{unloadingArea}
	and  SHIP_OWNER_ID=#{shipOwnerId}
	and  EP_MEMBER_NAME_EN=#{epMemberNameEn}
	and  SHIP_OWNER_EN=#{shipOwnerEn}
	and  CARRIER_NAME_EN=#{carrierNameEn}
	and  BROKER_NAME_EN=#{brokerNameEn}
	and  SYS_SHIP_UUID=#{sysShipUuid}
	ORDER BY CREATE_DATE DESC

select
count(*) 
from t_ship_ship_pact p,t_ship_agreement a where p.ALIVE_FLAG='1' and p.uuid  = a.ship_pact_uuid
AND p.EP_MEMBER_ID= #{epMemberId}
 and a.agreement_code = #{agreementCode}

