	and  SHIP_OWNER_EN=#{shipOwnerEn}
	and  CARRIER_NAME_EN=#{carrierNameEn}
	and  BROKER_NAME_EN=#{brokerNameEn}
	and  SYS_SHIP_UUID=#{sysShipUuid}

	and ship_plate_uuid IN (
		SELECT
			p.UUID
		FROM
			t_ship_ship_plate p
		WHERE
			p.ALIVE_FLAG = "1"
		AND p.EP_MEMBER_ID =#{member}
		)


	ORDER BY PACT_END DESC

select
* from t_ship_ship_pact
where
ALIVE_FLAG='1'
and  SHIP_INTENTION_UUID=#{shipIntentionUuid}
and  SHIP_PLATE_UUID=#{shipPlateUuid}

mapper.update:
	update t_ship_ship_pact

		STATUS= #{status}
 		,UPDATE_USER = #{updateUser}
		,UPDATE_DATE = SYSDATE()

	where
		 UUID=#{uuid}

mapper.select:
		SELECT 
						DISTINCT
						pact.SHIP_PACT_ID as "shipPactId" 
						 ,   pact.UUID as "uuid" 
						 ,   pact.PACT_CODE as "pactCode" 
						 ,   pact.EP_MEMBER_ID as "epMemberId" 
						 ,   pact.STATUS as "status" 
						 ,   pact.LOAD_PORT as "loadPort" 
						 ,   pact.DETAIL_STATUS as "detailStatus" 
						 ,   pact.SHIP_PLATE_ID as "shipPlateId" 
						 ,   pact.SHIP_PLATE_UUID as "shipPlateUuid" 
						 ,   pact.SIGN_DATE as "signDate" 
						 ,   pact.CARRIER_ID as "carrierId" 
						 ,	 pact.CARRIER_NAME as "carrierName" 
						 ,   pact.SHIP_OWNER as "shipOwner" 
						 ,   pact.SHIP_NAME as "shipName" 
						 ,   pact.PACT_BEG as "pactBeg" 
						 ,   pact.PACT_END as "pactEnd" 
						 ,   pact.PACT_TEXT as "pactText" 
						 ,   pact.MIN_QUANTITY as "minQuantity" 
						 ,   pact.WS as "ws" 
						 ,   pact.WS_RES as "wsRes" 
						 ,   pact.PACT_SPEED as "pactSpeed" 
						 ,   pact.DEMURRAGE as "demurrage" 
						 ,   pact.BROKER_ID as "brokerId" 
						 ,   pact.BROKER_NAME as "brokerName" 
						 ,   pact.DOCK_TIME as "dockTime" 
						 ,   pact.ACCESSORY as "accessory" 
						 ,   pact.ACCESSORY_PATH as "accessoryPath" 
						 ,   pact.MMSI as "mmsi" 
						 ,   pact.REMARK as "remark" 
						 ,   pact.ALIVE_FLAG as "aliveFlag" 
						 ,   pact.VERSION as "version" 
						 ,   pact.LANG_VER as "langVer" 
						 ,   pact.CREATE_DATE as "createDate" 
						 ,   pact.UPDATE_DATE as "updateDate" 
						 ,   pact.CREATE_USER as "createUser" 
						 ,   pact.UPDATE_USER as "updateUser" 
						 ,   pact.EXT1 as "ext1"
						 ,   pact.SHIP_INTENTION_ID as "shipIntentionId" 
						 ,   pact.SHIP_INTENTION_UUID as "shipIntentionUuid" 
 						 ,   pact.LOADING_AREA as "loadingArea" 
						 ,   pact.UNLOADING_AREA as "unloadingArea" 
						 ,   pact.SHIP_OWNER_ID as "shipOwnerId" 
						 ,   pact.EP_MEMBER_NAME_EN as "epMemberNameEn" 
						 ,   pact.SHIP_OWNER_EN as "shipOwnerEn" 
						 ,   pact.CARRIER_NAME_EN as "carrierNameEn" 
						 ,   pact.BROKER_NAME_EN as "brokerNameEn" 
						 ,   pact.SYS_SHIP_UUID as "sysShipUuid" 
						 FROM t_ship_agency AS agency 
						,t_ship_ship_pact AS pact 
		where
			pact.ALIVE_FLAG='1'
			AND agency.SHIP_PACT_UUID = pact.UUID 
			AND agency.EP_MEMBER_ID =#{epMemberId}
			and  pact.STATUS IN ('3','4','5','6','7','8')
			and  pact.SHIP_PACT_ID=#{shipPactId}
			and  pact.UUID=#{uuid}
			and pact.PACT_CODE like '%${pactCode}%'
			and  pact.LOAD_PORT=#{loadPort}
			and  pact.DETAIL_STATUS=#{detailStatus}
			and  pact.SHIP_PLATE_ID=#{shipPlateId}
			and  pact.SHIP_PLATE_UUID=#{shipPlateUuid}
			and  pact.SIGN_DATE=#{signDate}
			and  pact.CARRIER_ID=#{carrierId}
			and  pact.CARRIER_NAME=#{carrierName}
			and  pact.SHIP_OWNER=#{shipOwner}
			and  pact.SHIP_NAME like CONCAT(CONCAT('%',#{shipName}),'%')
			and  pact.PACT_BEG=#{pactBeg}
			and  pact.PACT_END=#{pactEnd}
			and  pact.PACT_TEXT=#{pactText}
			and  pact.MIN_QUANTITY=#{minQuantity}
			and  pact.WS=#{ws}
			and  pact.WS_RES=#{wsRes}
			and  pact.PACT_SPEED=#{pactSpeed}
			and  pact.DEMURRAGE=#{demurrage}
			and  pact.BROKER_ID=#{brokerId}
			and  pact.BROKER_NAME=#{brokerName}
			and  pact.DOCK_TIME=#{dockTime}
			and  pact.ACCESSORY=#{accessory}
			and  pact.ACCESSORY_PATH=#{accessoryPath}
			and  pact.MMSI=#{mmsi}
			and  pact.REMARK=#{remark}
			and  pact.ALIVE_FLAG=#{aliveFlag}
			and  pact.VERSION=#{version}
			and  pact.LANG_VER=#{langVer}
			and  pact.CREATE_DATE=#{createDate}
			and  pact.UPDATE_DATE=#{updateDate}
			and  pact.CREATE_USER=#{createUser}
			and  pact.UPDATE_USER=#{updateUser}
			and  pact.EXT1=#{ext1}
			and  pact.SHIP_INTENTION_ID=#{shipIntentionId}
			and  pact.SHIP_INTENTION_UUID=#{shipIntentionUuid}
			and  pact.SHIP_OWNER_ID=#{shipOwnerId}
			and  pact.SYS_SHIP_UUID=#{sysShipUuid}
			ORDER BY PACT_END DESC

select
				  pact.SHIP_PACT_ID as "shipPactId" 
				 ,   pact.UUID as "uuid" 
				 ,   pact.PACT_CODE as "pactCode" 
				 ,   pact.EP_MEMBER_ID as "epMemberId" 
				 ,   pact.STATUS as "status" 
				 ,   pact.LOAD_PORT as "loadPort" 
				 ,   pact.DETAIL_STATUS as "detailStatus" 
				 ,   pact.SHIP_PLATE_ID as "shipPlateId" 
				 ,   pact.SHIP_PLATE_UUID as "shipPlateUuid" 
				 ,   pact.SIGN_DATE as "signDate" 
				 ,   pact.CARRIER_ID as "carrierId" 
				 ,	 pact.CARRIER_NAME as "carrierName" 
				 ,   pact.SHIP_OWNER as "shipOwner" 
				 ,   pact.SHIP_NAME as "shipName" 
				 ,   pact.PACT_BEG as "pactBeg" 
				 ,   pact.PACT_END as "pactEnd" 
				 ,   pact.PACT_TEXT as "pactText" 
				 ,   pact.MIN_QUANTITY as "minQuantity" 
				 ,   pact.WS as "ws" 
				 ,   pact.WS_RES as "wsRes" 
				 ,   pact.PACT_SPEED as "pactSpeed" 
				 ,   pact.DEMURRAGE as "demurrage" 
				 ,   pact.BROKER_ID as "brokerId" 
				 ,   pact.BROKER_NAME as "brokerName" 
				 ,   pact.DOCK_TIME as "dockTime" 
				 ,   pact.ACCESSORY as "accessory" 
				 ,   pact.ACCESSORY_PATH as "accessoryPath" 
				 ,   pact.MMSI as "mmsi" 
				 ,   pact.REMARK as "remark" 
				 ,   pact.ALIVE_FLAG as "aliveFlag" 
				 ,   pact.VERSION as "version" 
				 ,   pact.LANG_VER as "langVer" 
				 ,   pact.CREATE_DATE as "createDate" 
				 ,   pact.UPDATE_DATE as "updateDate" 
				 ,   pact.CREATE_USER as "createUser" 
				 ,   pact.UPDATE_USER as "updateUser" 
				 ,   pact.EXT1 as "ext1"
				 ,   pact.SHIP_INTENTION_ID as "shipIntentionId" 
				 ,   pact.SHIP_INTENTION_UUID as "shipIntentionUuid" 
				 ,   pact.LOADING_AREA as "loadingArea" 
				 ,   pact.UNLOADING_AREA as "unloadingArea" 
				 ,   pact.SHIP_OWNER_ID as "shipOwnerId" 
				 ,   pact.EP_MEMBER_NAME_EN as "epMemberNameEn" 
				 ,   pact.SHIP_OWNER_EN as "shipOwnerEn" 
				 ,   pact.CARRIER_NAME_EN as "carrierNameEn" 
				 ,   pact.BROKER_NAME_EN as "brokerNameEn" 
				 ,   pact.SYS_SHIP_UUID as "sysShipUuid" 
				from t_ship_ship_pact pact,
					 t_ship_ship_plate t
where
	pact.ALIVE_FLAG='1'
	and pact.SHIP_PLATE_UUID = t.UUID
	and  t.REL_TYPE= '1'
	and ( t.REL_TYPE= '2' or t.REL_TYPE is null)
	and  pact.SHIP_PACT_ID=#{shipPactId}
	and  pact.UUID=#{uuid}
	and  pact.PACT_CODE=#{pactCode}
	and  pact.EP_MEMBER_ID=#{epMemberId}
	and  pact.STATUS=#{status}
	and  pact.LOAD_PORT=#{loadPort}
	and  pact.DETAIL_STATUS=#{detailStatus}
	and  pact.SHIP_PLATE_ID=#{shipPlateId}
	and  pact.SHIP_PLATE_UUID=#{shipPlateUuid}
	and  pact.SIGN_DATE=#{signDate}
	and  pact.CARRIER_ID=#{carrierId}
	and  pact.CARRIER_NAME=#{carrierName}
	and  pact.SHIP_OWNER=#{shipOwner}
	and  pact.SHIP_NAME=#{shipName}
	and  pact.PACT_BEG=#{pactBeg}
	and  pact.PACT_END=#{pactEnd}
	and  pact.PACT_TEXT=#{pactText}
	and  pact.MIN_QUANTITY=#{minQuantity}
	and  pact.WS=#{ws}
	and  pact.WS_RES=#{wsRes}
	and  pact.PACT_SPEED=#{pactSpeed}
	and  pact.DEMURRAGE=#{demurrage}
	and  pact.BROKER_ID=#{brokerId}
	and  pact.BROKER_NAME=#{brokerName}
	and  pact.DOCK_TIME=#{dockTime}
	and  pact.ACCESSORY=#{accessory}
	and  pact.ACCESSORY_PATH=#{accessoryPath}
	and  pact.MMSI=#{mmsi}
	and  pact.REMARK=#{remark}
	and  pact.ALIVE_FLAG=#{aliveFlag}
	and  pact.VERSION=#{version}
	and  pact.LANG_VER=#{langVer}
	and  pact.CREATE_DATE=#{createDate}
	and  pact.UPDATE_DATE=#{updateDate}
	and  pact.CREATE_USER=#{createUser}
	and  pact.UPDATE_USER=#{updateUser}
	and  pact.EXT1=#{ext1}
	and  pact.SHIP_INTENTION_ID=#{shipIntentionId}
	and  pact.SHIP_INTENTION_UUID=#{shipIntentionUuid}
	and  pact.LOADING_AREA=#{loadingArea}
	and  pact.UNLOADING_AREA=#{unloadingArea}
	and  pact.SHIP_OWNER_ID=#{shipOwnerId}
	and  pact.SYS_SHIP_UUID=#{sysShipUuid}
	ORDER BY  pact.STATUS ASC , pact.CREATE_DATE DESC
