SELECT confirmationSheet.* FROM t_ship_confirmation_sheet confirmationSheet
WHERE confirmationSheet.ALIVE_FLAG = 1


    AND confirmationSheet.CHARTER_PARTY_NUMBER = #{charterPartyNumber, jdbcType=VARCHAR}


    AND confirmationSheet.PRODUCT LIKE concat(concat('%',#{product}),'%')


    AND confirmationSheet.STATUS = #{status, jdbcType=VARCHAR}


     AND confirmationSheet.CREATE_DATE >= #{createDateStart, jdbcType=TIMESTAMP} 


     AND confirmationSheet.CREATE_DATE <= #{createDateEnd, jdbcType=TIMESTAMP} 

AND CONFIRMATION_SHEET_CD LIKE
    concat(concat('%',#{confirmationSheetCd}),'%')

AND EP_MEMBER_ID=#{epMemberId}
AND BUYER_ID=#{buyerId}
AND SELLER_ID=#{sellerId}
AND SHIPPING_AGENT_ID = #{shippingAgentId}
ORDER BY CREATE_DATE DESC

SELECT confirmationSheet.* FROM t_ship_confirmation_sheet confirmationSheet
WHERE confirmationSheet.ALIVE_FLAG = 1


    AND confirmationSheet.CHARTER_PARTY_NUMBER = #{charterPartyNumber, jdbcType=VARCHAR}


    AND confirmationSheet.PRODUCT LIKE concat(concat('%',#{product}),'%')


    AND confirmationSheet.STATUS = #{status, jdbcType=VARCHAR}


     AND confirmationSheet.CREATE_DATE >= #{createDateStart, jdbcType=TIMESTAMP} 


     AND confirmationSheet.CREATE_DATE <= #{createDateEnd, jdbcType=TIMESTAMP} 

AND CONFIRMATION_SHEET_CD LIKE
    concat(concat('%',#{confirmationSheetCd}),'%')

AND BUYER_ID=#{buyerId}
AND (SELLER_ID=#{sellerId} OR (SELLER_ID = #{singaporeId} AND PRODUCT_SOURCE_CODE in ('1','2')))
AND SHIPPING_AGENT_ID = #{shippingAgentId}

    and (PRODUCT LIKE "%"#{keywords}"%" or PORT_OF_LOADING like "%"#{keywords}"%" or PORT_OF_DISCHARGE like
    "%"#{keywords}"%")

ORDER BY CREATE_DATE DESC

