select
				  SHIP_LOAD_PORT_ID as "shipLoadPortId" 
				 ,   UUID as "uuid" 
				 ,   SHIP_CONFIRMATION_SHEET_ID as "shipConfirmationSheetId" 
				 ,   VESSEL_NAME as "vesselName" 
				 ,   PORT_OF_LOADING as "portOfLoading" 
				 ,   IMO as "imo" 
				 ,   PRODUCT as "product" 
				 ,   QUANTITY_TONS as "quantityTons" 
				 ,   QUANTITY as "quantity" 
				 ,   LAYCAN_START as "laycanStart" 
				 ,   LAYCAN_END as "laycanEnd" 

				 ,   NOR_TENDERED as "norTendered" 
				 ,   ANCHOR_AWEIGH as "anchorAweigh" 
				 ,   POB_DATETIME_ONE as "pobDatetimeOne" 
				 ,   POB_BERTH as "pobBerth" 
				 ,   FIRST_LINE_DATETIME as "firstLineDatetime" 
				 ,   FIRST_LINE_ETB as "firstLineEtb" 
				 ,   FIRST_LINE_BERTH as "firstLineBerth" 
				 ,   ALL_FAST as "allFast" 

				 ,   TANKS_INSPECTION_DATETIME as "tanksInspectionDatetime" 
				 ,   TANKS_INSPECTION_REMARKS as "tanksInspectionRemarks" 
				 ,   INDEPENDENT_INSPECTION as "independentInspection" 
				 ,   INDEPENDENT_INSPECTION_DATETIME as "independentInspectionDatetime" 
				 ,   INDEPENDENT_INSPECTION_REMARKS as "independentInspectionRemarks" 
				 ,   INDEPENDENT_INSPECTION_OBQ as "independentInspectionObq" 
				 ,   SHORE_ARMS_CONNECTING as "shoreArmsConnecting" 
				 ,   COMMENCED_LOADING_DATETIME as "commencedLoadingDatetime" 
				 ,   ETC as "etc" 
				 ,   COMPLETED_LOADING_DATETIME as "completedLoadingDatetime" 
				 ,   SHORE_ARM_DISCONNECTING as "shoreArmDisconnecting" 
				 ,   CARGO_SURVEY as "cargoSurvey" 
				 ,   BL_METRIC_TONS as "blMetricTons" 
				 ,   BL_LONG_TONS as "blLongTons" 
				 ,   LTR as "ltr" 
				 ,   BBLS as "bbls" 
				 ,   LEAVE_DATETIME as "leaveDatetime" 
				 ,   NEXT_PORT_ID as "nextPortId" 
				 ,   NEXT_PORT as "nextPort" 
				 ,   STEP as "step" 
				 ,   EXT1 as "ext1" 
				 ,   VERSION as "version" 
				 ,   ALIVE_FLAG as "aliveFlag" 
				 ,   CREATE_DATE as "createDate" 
				 ,   CREATE_USER as "createUser" 
				 ,   UPDATE_DATE as "updateDate" 
				 ,   UPDATE_USER as "updateUser" 
				from t_ship_load_port
where
	ALIVE_FLAG='1'
	and  SHIP_LOAD_PORT_ID=#{shipLoadPortId}
