mapper.insert:
 insert  into t_ship_load_port (				SHIP_LOAD_PORT_ID
					,UUID
					,SHIP_CONFIRMATION_SHEET_ID
					,VESSEL_NAME
					,PORT_OF_LOADING
					,IMO
					,PRODUCT
					,QUANTITY_TONS
					,QUANTITY
					,LAYCAN_START
					,LAYCAN_END
					,ALIVE_FLAG
					,CREATE_DATE
					,CREATE_USER
					,UPDATE_DATE
					,UPDATE_USER
					)
	values(		#{shipLoadPortId}
					,#{uuid}
					,#{shipConfirmationSheetId}
					,#{vesselName}
					,#{portOfLoading}
					,#{imo}
					,#{product}
					,#{quantityTons}
					,#{quantity}
					,#{laycanStart}
					,#{laycanEnd}
					,#{aliveFlag}
					,SYSDATE()
					,#{createUser}
					,SYSDATE()
					,#{updateUser}
			)
			ON DUPLICATE KEY UPDATE 
					LAYCAN_START=values(LAYCAN_START)
					,LAYCAN_END=values(LAYCAN_END)
					,UPDATE_DATE=values(UPDATE_DATE)
					,UPDATE_USER=values(UPDATE_USER);

